	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfGainsAndLossesFromInvestmentsInEquityInstrumentsMember' ,N'Reserve of gains and losses from investments in equity instruments [member]', N'This member stands for a component of equity representing accumulated gains and losses from investments in equity instruments that the entity has designated at fair value through other comprehensive income.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfChangeInFairValueOfFinancialLiabilityAttributableToChangeInCreditRiskOfLiabilityMember' ,N'Reserve of change in fair value of financial liability attributable to change in credit risk of liability [member]', N'This member stands for a component of equity representing the accumulated change in fair value of financial liabilities attributable to change in the credit risk of the liabilities. [Refer: Credit risk [member]; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveForCatastropheMember' ,N'Reserve for catastrophe [member]', N'This member stands for a component of equity representing resources to provide for infrequent but severe catastrophic losses caused by events such as damage to nuclear installations or satellites or earthquake damage.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveForEqualisationMember' ,N'Reserve for equalisation [member]', N'This member stands for a component of equity representing resources to cover random fluctuations of claim expenses around the expected value of claims for some types of insurance contract (for example, hail, credit, guarantee and fidelity insurance) using a formula based on experience over a number of years.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfDiscretionaryParticipationFeaturesMember' ,N'Reserve of discretionary participation features [member]', N'This member stands for a component of equity resulting from discretionary participation features. Discretionary participation features are contractual rights to receive, as a supplement to guaranteed benefits, additional benefits: (a) that are likely to be a significant portion of the total contractual benefits; (b) whose amount or timing is contractually at the discretion of the issuer; and (c) that are contractually based on: (i) the performance of a specified pool of contracts or a specified type of contract; (ii) realised and/or unrealised investment returns on a specified pool of assets held by the issuer; or (iii) the profit or loss of the company, fund or other entity that issues the contract.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RetainedEarningsMember' ,N'Retained earnings [member]', N'This member stands for a component of equity representing an entity''s cumulative undistributed earnings or deficit.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'NoncontrollingInterestsMember' ,N'Non-controlling interests [member]', N'This member stands for equity in a subsidiary not attributable, directly or indirectly, to the parent.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypesOfRisksAxis' ,N'Types of risks [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TypesOfRisksMember' ,N'Risks [member]', N'This member stands for all types of risks. It also represents the standard value for the ''Types of risks'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CreditRiskMember' ,N'Credit risk [member]', N'This member stands for the risk that one party to a financial instrument will cause a financial loss for the other party by failing to discharge an obligation. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'LiquidityRiskMember' ,N'Liquidity risk [member]', N'This member stands for the risk that an entity will encounter difficulty in meeting obligations associated with financial liabilities that are settled by delivering cash or another financial asset. [Refer: Financial assets; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MarketRiskMember' ,N'Market risk [member]', N'This member stands for the risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in market prices. Market risk comprises three types of risk: currency risk, interest rate risk and other price risk. [Refer: Currency risk [member]; Interest rate risk [member]; Other price risk [member]; Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CurrencyRiskMember' ,N'Currency risk [member]', N'This member stands for a type of market risk representing the risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in foreign exchange rates. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestRateRiskMember' ,N'Interest rate risk [member]', N'This member stands for the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in market interest rates. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherPriceRiskMember' ,N'Other price risk [member]', N'This member stands for a type of market risk representing the risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in market prices (other than those arising from interest rate risk or currency risk), whether those changes are caused by factors specific to the individual financial instrument or its issuer, or by factors affecting all similar financial instruments traded in the market. [Refer: Currency risk [member]; Interest rate risk [member]; Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EquityPriceRiskMember' ,N'Equity price risk [member]', N'This member stands for a component of other price risk that represents the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in equity prices. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'CommodityPriceRiskMember' ,N'Commodity price risk [member]', N'This member stands for a component of other price risk that represents the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in commodity prices. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'PrepaymentRiskMember' ,N'Prepayment risk [member]', N'This member stands for the type of risk that one party to a financial asset will incur a financial loss because the other party repays earlier or later than expected. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ResidualValueRiskMember' ,N'Residual value risk [member]', N'This member stands for a component of other price risk that represents the type of risk that the fair value or future cash flows of a financial instrument will fluctuate because of changes in residual values. [Refer: Financial instruments, class [member]]');
