	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTermsOfSharesReservedForIssueUnderOptionsAndContractsForSaleOfShares' ,N'Description of terms of shares reserved for issue under options and contracts for sale of shares', N'The description of terms of shares reserved for issue under options and contracts for the sale of shares.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReservesAndOtherEquityInterestExplanatory' ,N'Disclosure of reserves within equity [text block]', N'The disclosure of reserves within equity. [Refer: Other reserves [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReservesWithinEquityTable' ,N'Disclosure of reserves within equity [table]', N'Schedule disclosing information related to reserves within equity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReservesWithinEquityAxis' ,N'Reserves within equity [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherReservesMember' ,N'Other reserves [member]', N'This member stands for a component of equity representing reserves within equity, not including retained earnings. It also represents the standard value for the ''Reserves within equity'' axis if no other member is used. [Refer: Retained earnings]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RevaluationSurplusMember' ,N'Revaluation surplus [member]', N'This member stands for a component of equity representing accumulated revaluation surplus on the revaluation of assets recognised in other comprehensive income. [Refer: Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfExchangeDifferencesOnTranslationMember' ,N'Reserve of exchange differences on translation [member]', N'This member stands for a component of equity representing accumulated exchange differences on the translation of financial statements of foreign operations recognised in other comprehensive income. [Refer: Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfCashFlowHedgesMember' ,N'Reserve of cash flow hedges [member]', N'This member stands for a component of equity representing the accumulated portion of gain (loss) on a hedging instrument that is determined to be an effective hedge for cash flow hedges. [Refer: Cash flow hedges [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfGainsAndLossesOnHedgingInstrumentsThatHedgeInvestmentsInEquityInstrumentsMember' ,N'Reserve of gains and losses on hedging instruments that hedge investments in equity instruments [member]', N'This member stands for a component of equity representing the accumulated gains and losses on hedging instruments that hedge investments in equity instruments that the entity has designated at fair value through other comprehensive income.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfChangeInValueOfTimeValueOfOptionsMember' ,N'Reserve of change in value of time value of options [member]', N'This member stands for a component of equity representing the accumulated change in the value of the time value of options when separating the intrinsic value and time value of an option contract and designating as the hedging instrument only the changes in the intrinsic value.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfChangeInValueOfForwardElementsOfForwardContractsMember' ,N'Reserve of change in value of forward elements of forward contracts [member]', N'This member stands for a component of equity representing the accumulated change in the value of the forward elements of forward contracts when separating the forward element and spot element of a forward contract and designating as the hedging instrument only the changes in the spot element.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfChangeInValueOfForeignCurrencyBasisSpreadsMember' ,N'Reserve of change in value of foreign currency basis spreads [member]', N'This member stands for a component of equity representing the accumulated change in the value of foreign currency basis spreads of financial instruments when excluding them from the designation of these financial instruments as hedging instruments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfGainsAndLossesOnFinancialAssetsMeasuredAtFairValueThroughOtherComprehensiveIncomeMember' ,N'Reserve of gains and losses on financial assets measured at fair value through other comprehensive income [member]', N'This member stands for a component of equity representing the accumulated gains and losses on financial assets measured at fair value through other comprehensive income. [Refer: Financial assets measured at fair value through other comprehensive income; Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfInsuranceFinanceIncomeExpensesFromInsuranceContractsIssuedExcludedFromProfitOrLossThatWillBeReclassifiedToProfitOrLossMember' ,N'Reserve of insurance finance income (expenses) from insurance contracts issued excluded from profit or loss that will be reclassified to profit or loss [member]', N'This member stands for a component of equity representing the accumulated insurance finance income (expenses) from insurance contracts issued excluded from profit or loss that will be reclassified subsequently to profit or loss. [Refer: Insurance finance income (expenses); Insurance contracts issued [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfInsuranceFinanceIncomeExpensesFromInsuranceContractsIssuedExcludedFromProfitOrLossThatWillNotBeReclassifiedToProfitOrLossMember' ,N'Reserve of insurance finance income (expenses) from insurance contracts issued excluded from profit or loss that will not be reclassified to profit or loss [member]', N'This member stands for a component of equity representing the accumulated insurance finance income (expenses) from insurance contracts issued excluded from profit or loss that will not be reclassified subsequently to profit or loss. [Refer: Insurance finance income (expenses); Insurance contracts issued [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfFinanceIncomeExpensesFromReinsuranceContractsHeldExcludedFromProfitOrLossMember' ,N'Reserve of finance income (expenses) from reinsurance contracts held excluded from profit or loss [member]', N'This member stands for a component of equity representing the accumulated finance income (expenses) from reinsurance contracts held excluded from profit or loss. [Refer: Insurance finance income (expenses); Reinsurance contracts held [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfGainsAndLossesOnRemeasuringAvailableforsaleFinancialAssetsMember' ,N'Reserve of gains and losses on remeasuring available-for-sale financial assets [member]', N'This member stands for a component of equity representing accumulated gains and losses on remeasuring available-for-sale financial assets. [Refer: Financial assets available-for-sale]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfSharebasedPaymentsMember' ,N'Reserve of share-based payments [member]', N'This member stands for a component of equity resulting from share-based payments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfRemeasurementsOfDefinedBenefitPlansMember' ,N'Reserve of remeasurements of defined benefit plans [member]', N'This member stands for a component of equity resulting from remeasurements of defined benefit plans. [Refer: Other comprehensive income, net of tax, gains (losses) on remeasurements of defined benefit plans]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AmountRecognisedInOtherComprehensiveIncomeAndAccumulatedInEquityRelatingToNoncurrentAssetsOrDisposalGroupsHeldForSaleMember' ,N'Amount recognised in other comprehensive income and accumulated in equity relating to non-current assets or disposal groups held for sale [member]', N'This member stands for a component of equity resulting from amounts that are recognised in other comprehensive income and accumulated in equity that relate to non-current assets or disposal groups held for sale. [Refer: Non-current assets or disposal groups classified as held for sale; Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfGainsAndLossesFromInvestmentsInEquityInstrumentsMember' ,N'Reserve of gains and losses from investments in equity instruments [member]', N'This member stands for a component of equity representing accumulated gains and losses from investments in equity instruments that the entity has designated at fair value through other comprehensive income.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfChangeInFairValueOfFinancialLiabilityAttributableToChangeInCreditRiskOfLiabilityMember' ,N'Reserve of change in fair value of financial liability attributable to change in credit risk of liability [member]', N'This member stands for a component of equity representing the accumulated change in fair value of financial liabilities attributable to change in the credit risk of the liabilities. [Refer: Credit risk [member]; Financial liabilities]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveForCatastropheMember' ,N'Reserve for catastrophe [member]', N'This member stands for a component of equity representing resources to provide for infrequent but severe catastrophic losses caused by events such as damage to nuclear installations or satellites or earthquake damage.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveForEqualisationMember' ,N'Reserve for equalisation [member]', N'This member stands for a component of equity representing resources to cover random fluctuations of claim expenses around the expected value of claims for some types of insurance contract (for example, hail, credit, guarantee and fidelity insurance) using a formula based on experience over a number of years.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReserveOfDiscretionaryParticipationFeaturesMember' ,N'Reserve of discretionary participation features [member]', N'This member stands for a component of equity resulting from discretionary participation features. Discretionary participation features are contractual rights to receive, as a supplement to guaranteed benefits, additional benefits: (a) that are likely to be a significant portion of the total contractual benefits; (b) whose amount or timing is contractually at the discretion of the issuer; and (c) that are contractually based on: (i) the performance of a specified pool of contracts or a specified type of contract; (ii) realised and/or unrealised investment returns on a specified pool of assets held by the issuer; or (iii) the profit or loss of the company, fund or other entity that issues the contract.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfReservesWithinEquityLineItems' ,N'Disclosure of reserves within equity [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureAndPurposeOfReservesWithinEquity' ,N'Description of nature and purpose of reserves within equity', N'The description of the nature and purpose of reserves within equity. [Refer: Other reserves]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfOtherEquityInterest' ,N'Description of rights, preferences and restrictions attaching to category of equity interest by entity without share capital', N'The description of the rights, preferences and restrictions that are attached to a category of equity interest by an entity without share capital. [Refer: Share capital [member]; Other equity interest]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SummaryQuantitativeDataAboutPuttableFinancialInstrumentsClassifiedAsEquityInstruments' ,N'Summary quantitative data about puttable financial instruments classified as equity instruments', N'Summary quantitative data about puttable financial instruments classified as equity instruments. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutObjectivesPoliciesAndProcessesForManagingEntitysObligationToRepurchaseOrRedeemPuttableFinancialInstruments' ,N'Information about objectives, policies and processes for managing entity''s obligation to repurchase or redeem puttable financial instruments', N'Information about the entity''s objectives, policies, and processes for managing its obligation to repurchase or redeem puttable financial instruments classified as equity instruments when required to do so by the instrument holders, including any changes from the previous period. [Refer: Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ExpectedCashOutflowOnRedemptionOrRepurchaseOfPuttableFinancialInstruments' ,N'Expected cash outflow on redemption or repurchase of puttable financial instruments', N'The expected cash outflow on the redemption or repurchase of puttable financial instruments classified as equity. [Refer: Liquidity risk [member]; Financial instruments, class [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InformationAboutHowExpectedCashOutflowOnRedemptionOrRepurchaseWasDetermined' ,N'Information about how expected cash outflow on redemption or repurchase was determined', N'Information about how the expected cash outflow on redemption or repurchase of puttable financial instruments classified as equity was determined. [Refer: Expected cash outflow on redemption or repurchase of puttable financial instruments]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfInterestInFundsExplanatory' ,N'Disclosure of interest in funds [text block]', N'The entire disclosure for the entity''s interest in decommissioning, restoration and environmental rehabilitation funds.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfInterestInFunds' ,N'Description of nature of interest in funds', N'The description of the nature of the entity''s interest in decommissioning, restoration and environmental rehabilitation funds.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RestrictionsOnAccessToAssetsInFunds' ,N'Description of restrictions on access to assets in funds', N'The description of restrictions on access to the assets in decommissioning, restoration and environmental rehabilitation funds.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfRedemptionProhibitionTransferBetweenFinancialLiabilitiesAndEquityExplanatory' ,N'Disclosure of redemption prohibition, transfer between financial liabilities and equity [text block]', N'The entire disclosure for the change in a redemption prohibition that leads to a transfer between financial liabilities and equity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TransferBetweenFinancialLiabilitiesAndEquityAttributableToChangeInRedemptionProhibition' ,N'Transfer between financial liabilities and equity attributable to change in redemption prohibition', N'The amount transferred between financial liabilities and the equity attributable to a change in the redemption prohibition.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'TimingAndReasonForTransferBetweenFinancialLiabilitiesAndEquityAttributableToChangeInRedemptionProhibition' ,N'Description of timing and reason for transfer between financial liabilities and equity attributable to change in redemption prohibition', N'The description of the timing of, and the reason for, the transfer between financial liabilities and the equity attributable to a change in the redemption prohibition.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfEntitysReportableSegmentsExplanatory' ,N'Disclosure of entity''s operating segments [text block]', N'The entire disclosure for operating segments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'FactorsUsedToIdentifyEntitysReportableSegments' ,N'Description of factors used to identify entity''s reportable segments', N'The description of the factors used to identify the entity''s reportable segments, including the basis of organisation (for example, whether management has chosen to organise the entity around differences in products and services, geographical areas, regulatory environments or a combination of factors and whether operating segments have been aggregated). [Refer: Geographical areas [member]; Operating segments [member]; Products and services [member]; Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfJudgementsMadeByManagementInApplyingAggregationCriteriaForOperatingSegments' ,N'Description of judgements made by management in applying aggregation criteria for operating segments', N'The description of judgements made by the management in applying the aggregation criteria for operating segments. [Refer: Operating segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfTypesOfProductsAndServicesFromWhichEachReportableSegmentDerivesItsRevenues' ,N'Description of types of products and services from which each reportable segment derives its revenues', N'The description of the types of products and services from which a reportable segment derives its revenues. [Refer: Products and services [member]; Revenue]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfSourcesOfRevenueForAllOtherSegments' ,N'Description of sources of revenue for all other segments', N'The description of the sources of the revenue included in the ''all other segments'' category, which includes the combined information about other business activities and operating segments that are not reportable. [Refer: All other segments [member]; Revenue]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfBasisOfAccountingForTransactionsBetweenReportableSegments' ,N'Description of basis of accounting for transactions between reportable segments', N'The description of the basis of accounting for transactions between the entity''s reportable segments. [Refer: Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfDifferencesBetweenMeasurementsOfReportableSegmentsProfitsOrLossesAndEntitysProfitOrLossBeforeIncomeTaxExpenseOrIncomeAndDiscontinuedOperations' ,N'Description of nature of differences between measurements of reportable segments'' profits or losses and entity''s profit or loss before income tax expense or income and discontinued operations', N'The description of the nature of the differences between measurements of the reportable segments'' profits or losses and the entity''s profit or loss before income tax expense or income and discontinued operations. Those differences could include accounting policies and policies for the allocation of centrally incurred costs that are necessary for an understanding of the reported segment information. [Refer: Discontinued operations [member]; Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfDifferencesBetweenMeasurementsOfReportableSegmentsAssetsAndEntitysAssets' ,N'Description of nature of differences between measurements of reportable segments'' assets and entity''s assets', N'The description of the nature of the differences between measurements of the reportable segments'' assets and the entity''s assets. Those differences could include accounting policies and policies for allocation of jointly used assets that are necessary for an understanding of the reported segment information. [Refer: Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfDifferencesBetweenMeasurementsOfReportableSegmentsLiabilitiesAndEntitysLiabilities' ,N'Description of nature of differences between measurements of reportable segments'' liabilities and entity''s liabilities', N'The description of the nature of the differences between measurements of the reportable segments'' liabilities and the entity''s liabilities. Those differences could include accounting policies and policies for the allocation of jointly utilised liabilities that are necessary for an understanding of the reported segment information. [Refer: Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureOfChangesFromPriorPeriodsInMeasurementMethodsUsedToDetermineReportedSegmentProfitOrLossAndEffectOfThoseChangesOnMeasureOfSegmentProfitOrLoss' ,N'Description of nature of changes from prior periods in measurement methods used to determine reported segment profit or loss and effect of those changes on measure of segment profit or loss', N'The description of the nature of the changes from prior periods in the measurement methods used to determine reported segment profit or loss and the effect, if any, of those changes on the measure of segment profit (loss). [Refer: Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DescriptionOfNatureAndEffectOfAnyAsymmetricalAllocationsToReportableSegments' ,N'Description of nature and effect of any asymmetrical allocations to reportable segments', N'The description of the nature and effect of any asymmetrical allocations to reportable segments. For example, an entity might allocate depreciation expense to a segment without allocating the related depreciable assets to that segment. [Refer: Reportable segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfOperatingSegmentsExplanatory' ,N'Disclosure of operating segments [text block]', N'The disclosure of operating segments. [Refer: Operating segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfOperatingSegmentsTable' ,N'Disclosure of operating segments [table]', N'Schedule disclosing information related to operating segments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentConsolidationItemsAxis' ,N'Segment consolidation items [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EntitysTotalForSegmentConsolidationItemsMember' ,N'Entity''s total for segment consolidation items [member]', N'This member stands for the standard value for the ''Segment consolidation items'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OperatingSegmentsMember' ,N'Operating segments [member]', N'This member stands for operating segments. An operating segment is a component of an entity: (a) that engages in business activities from which it may earn revenues and incur expenses (including revenues and expenses relating to transactions with other components of the same entity); (b) whose operating results are regularly reviewed by the entity’s chief operating decision maker to make decisions about resources to be allocated to the segment and assess its performance; and (c) for which discrete financial information is available. [Refer: Revenue]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'MaterialReconcilingItemsMember' ,N'Material reconciling items [member]', N'This member stands for material adjustments used to reconcile items in the entity''s financial statements.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'EliminationOfIntersegmentAmountsMember' ,N'Elimination of intersegment amounts [member]', N'This member stands for the elimination of intersegment amounts in the reconciliations of total segment amounts to amounts recognised in the entity''s financial statements.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'UnallocatedAmountsMember' ,N'Unallocated amounts [member]', N'This member stands for items that have not been allocated to operating segments.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsAxis' ,N'Segments [axis]', N'The axis of a table defines the relationship between the domain members or categories in the table and the line items or concepts that complete the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'SegmentsMember' ,N'Segments [member]', N'This member stands for all segments of an entity. It also represents the standard value for the ''Segments'' axis if no other member is used.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReportableSegmentsMember' ,N'Reportable segments [member]', N'This member stands for operating segments for which Ifrs 8 requires information to be disclosed. The entity shall report separately information about an operating segment that meets any of the following quantitative thresholds: (a) reported revenue, including both sales to external customers and intersegment sales or transfers, is 10 per cent or more of the combined revenue, internal and external, of all operating segments; (b) the absolute amount of its reported profit or loss is 10 per cent or more of the greater, in absolute amount, of (i) the combined reported profit of all operating segments that did not report a loss and (ii) the combined reported loss of all operating segments that reported a loss; or (c) assets are 10 per cent or more of the combined assets of all operating segments. Additionally operating segments that do not meet any of the quantitative thresholds may be considered reportable, and separately disclosed, if management believes that information about the segment would be useful to users of the financial statements. [Refer: Operating segments [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AllOtherSegmentsMember' ,N'All other segments [member]', N'This member stands for business activities and operating segments that are not reportable.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DisclosureOfOperatingSegmentsLineItems' ,N'Disclosure of operating segments [line items]', N'Line items represent concepts included in a table. These concepts are used to disclose reportable information associated with domain members defined in one or many axes of the table.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Revenue' ,N'Revenue', N'Expiry date 2020-01-01: The income arising in the course of an entity''s ordinary activities. Income is increases in economic benefits during the accounting period in the form of inflows or enhancements of assets or decreases of liabilities that result in an increase in equity, other than those relating to contributions from equity participants.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'RevenueFromInterest' ,N'Interest income', N'The amount of income arising from interest.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestExpense' ,N'Interest expense', N'The amount of expense arising from interest.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InterestRevenueExpense' ,N'Interest income (expense)', N'The amount of income or expense arising from interest. [Refer: Interest expense; Interest income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'DepreciationAndAmortisationExpense' ,N'Depreciation and amortisation expense', N'The amount of depreciation and amortisation expense. Depreciation and amortisation are the systematic allocations of depreciable amounts of assets over their useful lives.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ShareOfProfitLossOfAssociatesAndJointVenturesAccountedForUsingEquityMethod' ,N'Share of profit (loss) of associates and joint ventures accounted for using equity method', N'The entity''s share of the profit (loss) of associates and joint ventures accounted for using the equity method. [Refer: Associates [member]; Investments accounted for using equity method; Joint ventures [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'IncomeTaxExpenseContinuingOperations' ,N'Tax expense (income)', N'The aggregate amount included in the determination of profit (loss) for the period in respect of current tax and deferred tax. [Refer: Current tax expense (income); Deferred tax expense (income)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'OtherMaterialNoncashItems' ,N'Other material non-cash items', N'The amount of material non-cash items other than depreciation and amortisation.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossBeforeTax' ,N'Profit (loss) before tax', N'The profit (loss) before tax expense or income. [Refer: Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLossFromContinuingOperations' ,N'Profit (loss) from continuing operations', N'The profit (loss) from continuing operations. [Refer: Continuing operations [member]; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ProfitLoss' ,N'Profit (loss)', N'The total of income less expenses from continuing and discontinued operations, excluding the components of other comprehensive income. [Refer: Other comprehensive income]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Assets' ,N'Assets', N'Expiry date 2020-01-01: The amount of resources: (a) controlled by the entity as a result of past events; and (b) from which future economic benefits are expected to flow to the entity.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'InvestmentAccountedForUsingEquityMethod' ,N'Investments accounted for using equity method', N'The amount of investments accounted for using the equity method. The equity method is a method of accounting whereby the investment is initially recognised at cost and adjusted thereafter for the post-acquisition change in the investor''s share of net assets of the investee. The investor''s profit or loss includes its share of the profit or loss of the investee. The investor''s other comprehensive income includes its share of the other comprehensive income of the investee. [Refer: At cost [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'AdditionsToNoncurrentAssets' ,N'Additions to non-current assets other than financial instruments, deferred tax assets, net defined benefit assets, and rights arising under insurance contracts', N'The amount of additions to non-current assets other than financial instruments, deferred tax assets, net defined benefit assets and rights arising under insurance contracts. [Refer: Deferred tax assets; Financial instruments, class [member]; Non-current assets; Types of insurance contracts [member]]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'Liabilities' ,N'Liabilities', N'Expiry date 2020-01-01: The amount of present obligations of the entity arising from past events, the settlement of which is expected to result in an outflow from the entity of resources embodying economic benefits.');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ImpairmentLossRecognisedInProfitOrLoss' ,N'Impairment loss recognised in profit or loss', N'The amount of impairment loss recognised in profit or loss. [Refer: Impairment loss; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ReversalOfImpairmentLossRecognisedInProfitOrLoss' ,N'Reversal of impairment loss recognised in profit or loss', N'The amount of reversal of impairment loss recognised in profit or loss. [Refer: Reversal of impairment loss; Profit (loss)]');
	INSERT INTO @IfrsConcepts([Id], [Label], [Documentation]) VALUES (N'ImpairmentLossRecognisedInOtherComprehensiveIncome' ,N'Impairment loss recognised in other comprehensive income', N'The amount of impairment loss recognised in other comprehensive income. Impairment loss on a revalued asset is recognised in other comprehensive income to the extent that the impairment loss does not exceed the amount in the revaluation surplus for that same asset. [Refer: Impairment loss; Revaluation surplus; Other comprehensive income]');
